import{_ as e,o,c as n,ak as a}from"./chunks/framework.CRk2WtWN.js";const g=JSON.parse('{"title":"Ecosystem","description":"","frontmatter":{},"headers":[],"relativePath":"ecosystem/ecosystem.md","filePath":"ecosystem/ecosystem.md"}'),r={name:"ecosystem/ecosystem.md"};function l(t,s,i,p,c,d){return o(),n("div",null,[...s[0]||(s[0]=[a(`<h1 id="ecosystem" tabindex="-1">Ecosystem <a class="header-anchor" href="#ecosystem" aria-label="Permalink to “Ecosystem”">​</a></h1><nav class="table-of-contents"><ul><li><a href="#soundworks-core">@soundworks/core</a></li><li><a href="#soundworks-plugin-audio-buffer-loader">@soundworks/plugin-audio-buffer-loader</a></li><li><a href="#soundworks-plugin-checkin">@soundworks/plugin-checkin</a></li><li><a href="#soundworks-plugin-filesystem">@soundworks/plugin-filesystem</a></li><li><a href="#soundworks-plugin-logger">@soundworks/plugin-logger</a></li><li><a href="#soundworks-plugin-platform">@soundworks/plugin-platform</a></li><li><a href="#soundworks-plugin-position">@soundworks/plugin-position</a></li><li><a href="#soundworks-plugin-scripting">@soundworks/plugin-scripting</a></li><li><a href="#soundworks-plugin-sync">@soundworks/plugin-sync</a></li><li><a href="#soundworks-template">@soundworks-template</a></li><li><a href="#soundworks-template-build">@soundworks/template-build</a></li><li><a href="#soundworks-template-helpers">@soundworks/template-helpers</a></li></ul></nav><h2 id="soundworks-core" tabindex="-1"><code>@soundworks/core</code> <a class="header-anchor" href="#soundworks-core" aria-label="Permalink to “@soundworks/core”">​</a></h2><p>The core of the framework is principally dedicated to:</p><ul><li>WebSocket communications</li><li>Distributed state management</li><li>Plugin hosting</li></ul><div class="tip custom-block"><p class="custom-block-title">Note</p><p>Most of the time you won&#39;t need to install <code>soundworks</code> manually, as the application template already takes care of that.</p></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#AE81FF;"> --save</span><span style="color:#E6DB74;"> @soundworks/core</span></span></code></pre></div><p><a href="http://collective-soundworks.github.io/soundworks/" target="_blank" rel="noreferrer"><strong>→ documentation</strong></a><br><a href="https://github.com/collective-soundworks/soundworks" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/core" target="_blank" rel="noreferrer"><strong>→ npm</strong></a></p><p><code>soundworks/core</code> functionalities can be extended by installing plugins. Here is a list of available plugins so far:</p><h2 id="soundworks-plugin-audio-buffer-loader" tabindex="-1"><code>@soundworks/plugin-audio-buffer-loader</code> <a class="header-anchor" href="#soundworks-plugin-audio-buffer-loader" aria-label="Permalink to “@soundworks/plugin-audio-buffer-loader”">​</a></h2><p><em><code>soundworks</code> plugin for loading and managing audio buffers and related JSON data. Currently limited to <code>.mp3</code>, <code>.wav</code> or <code>.json</code> formats to enforce browser compatibility and support (meaning support for Safari...)</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#E6DB74;"> @soundworks/plugin-audio-buffer-loader</span><span style="color:#AE81FF;"> --save</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-plugin-audio-buffer-loader" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/plugin-audio-buffer-loader" target="_blank" rel="noreferrer"><strong>→ npm</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-audio-buffer-loader" target="_blank" rel="noreferrer"><strong>→ example</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-audio-buffer-loader-and-filesystem" target="_blank" rel="noreferrer"><strong>→ example (w/ filesystem)</strong></a></p><h2 id="soundworks-plugin-checkin" tabindex="-1"><code>@soundworks/plugin-checkin</code> <a class="header-anchor" href="#soundworks-plugin-checkin" aria-label="Permalink to “@soundworks/plugin-checkin”">​</a></h2><p><em><code>soundworks</code> plugin for assigning a ticket (unique index) to the client among the available ones. The number of available tickets can be limited and tickets can be associated with additional data.</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#E6DB74;"> @soundworks/plugin-checkin</span><span style="color:#AE81FF;"> --save</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-plugin-checkin" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/plugin-checkin" target="_blank" rel="noreferrer"><strong>→ npm</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-checkin" target="_blank" rel="noreferrer"><strong>→ example</strong></a></p><h2 id="soundworks-plugin-filesystem" tabindex="-1"><code>@soundworks/plugin-filesystem</code> <a class="header-anchor" href="#soundworks-plugin-filesystem" aria-label="Permalink to “@soundworks/plugin-filesystem”">​</a></h2><p><em><code>soundworks</code> plugin to parse and watch directories and distribute their content to all clients in real-time.</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#E6DB74;"> @soundworks/plugin-filesystem</span><span style="color:#AE81FF;"> --save</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-plugin-filesystem" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/plugin-filesystem" target="_blank" rel="noreferrer"><strong>→ npm</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-filesystem" target="_blank" rel="noreferrer"><strong>→ example</strong></a></p><h2 id="soundworks-plugin-logger" tabindex="-1"><code>@soundworks/plugin-logger</code> <a class="header-anchor" href="#soundworks-plugin-logger" aria-label="Permalink to “@soundworks/plugin-logger”">​</a></h2><p><em><code>soundworks</code> plugin dedicated to recording arbitrarily from any node of the network into plain old files.</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#E6DB74;"> @soundworks/plugin-logger</span><span style="color:#AE81FF;"> --save</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-plugin-logger" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/plugin-logger" target="_blank" rel="noreferrer"><strong>→ npm</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-logger" target="_blank" rel="noreferrer"><strong>→ example</strong></a></p><h2 id="soundworks-plugin-platform" tabindex="-1"><code>@soundworks/plugin-platform</code> <a class="header-anchor" href="#soundworks-plugin-platform" aria-label="Permalink to “@soundworks/plugin-platform”">​</a></h2><p><em><code>soundworks</code> plugin that checks the availability of features required by the application, and their initialisation. It also provides an entry point for features that may require a user gesture (e.g. clicking on the screen to resume an audio context). The plugin can also be used to simply add a splash screen to the application.</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#E6DB74;"> @soundworks/plugin-platform</span><span style="color:#AE81FF;"> --save</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-plugin-platform" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/plugin-platform" target="_blank" rel="noreferrer"><strong>→ npm</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-platform" target="_blank" rel="noreferrer"><strong>→ example</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-platform-devicemotion" target="_blank" rel="noreferrer"><strong>→ example (w/ devicemotion)</strong></a></p><h2 id="soundworks-plugin-position" tabindex="-1"><code>@soundworks/plugin-position</code> <a class="header-anchor" href="#soundworks-plugin-position" aria-label="Permalink to “@soundworks/plugin-position”">​</a></h2><p><em><code>soundworks</code> plugin for locating people in an area. In the <code>soundworks-template</code> default views, the plugin is associated with an initialization screen where people are asked to provide their position on a map.</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#E6DB74;"> @soundworks/plugin-position</span><span style="color:#AE81FF;"> --save</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-plugin-position" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/plugin-position" target="_blank" rel="noreferrer"><strong>→ npm</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-position" target="_blank" rel="noreferrer"><strong>→ example</strong></a></p><h2 id="soundworks-plugin-scripting" tabindex="-1"><code>@soundworks/plugin-scripting</code> <a class="header-anchor" href="#soundworks-plugin-scripting" aria-label="Permalink to “@soundworks/plugin-scripting”">​</a></h2><p><em><code>soundworks</code> plugin for runtime scripting. The plugin allows to define an entry point of the application that enables the end user to modify the behavior of the distributed application at runtime, following an end-user programming strategy.</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#E6DB74;"> @soundworks/plugin-scripting</span><span style="color:#AE81FF;"> --save</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-plugin-scripting" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/plugin-scripting" target="_blank" rel="noreferrer"><strong>→ npm</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-scripting" target="_blank" rel="noreferrer"><strong>→ example</strong></a></p><h2 id="soundworks-plugin-sync" tabindex="-1"><code>@soundworks/plugin-sync</code> <a class="header-anchor" href="#soundworks-plugin-sync" aria-label="Permalink to “@soundworks/plugin-sync”">​</a></h2><p><em><code>soundworks</code> plugin for synchronizing arbitrary clocks across devices. The plugin is basically a wrapper around the <a href="https://github.com/collective-soundworks/sync" target="_blank" rel="noreferrer">@ircam/sync</a> library.</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#E6DB74;"> @soundworks/plugin-sync</span><span style="color:#AE81FF;"> --save</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-plugin-sync" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/plugin-sync" target="_blank" rel="noreferrer"><strong>→ npm</strong></a><br><a href="https://github.com/collective-soundworks/soundworks-examples/tree/master/plugin-sync" target="_blank" rel="noreferrer"><strong>→ example</strong></a></p><h2 id="soundworks-template" tabindex="-1"><code>@soundworks-template</code> <a class="header-anchor" href="#soundworks-template" aria-label="Permalink to “@soundworks-template”">​</a></h2><p><em>Project template for developing <code>soundworks</code> applications. The template provides a clean code base to start developing an application.</em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">git</span><span style="color:#E6DB74;"> clone</span><span style="color:#E6DB74;"> https://github.com/collective-soundworks/soundworks-template.git</span><span style="color:#E6DB74;"> my-application</span></span>
<span class="line"><span style="color:#66D9EF;">cd</span><span style="color:#E6DB74;"> my-application</span></span>
<span class="line"><span style="color:#A6E22E;">rm</span><span style="color:#AE81FF;"> -Rf</span><span style="color:#E6DB74;"> .git</span></span>
<span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span></span>
<span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> run</span><span style="color:#E6DB74;"> dev</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-template" target="_blank" rel="noreferrer"><strong>→ github</strong></a></p><p>Additionally to the <code>@soundworks/core</code> package, the template installs and uses the <code>@soundworks/template-build</code> and <code>@soundworks/template-helpers</code> to simplify a set of common tasks.</p><h2 id="soundworks-template-build" tabindex="-1"><code>@soundworks/template-build</code> <a class="header-anchor" href="#soundworks-template-build" aria-label="Permalink to “@soundworks/template-build”">​</a></h2><p><em>Build scripts of the <code>soundworks-template</code></em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#AE81FF;"> --save</span><span style="color:#E6DB74;"> @soundworks/template-build</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-template-build" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/template-build" target="_blank" rel="noreferrer"><strong>→ npm</strong></a></p><h2 id="soundworks-template-helpers" tabindex="-1"><code>@soundworks/template-helpers</code> <a class="header-anchor" href="#soundworks-template-helpers" aria-label="Permalink to “@soundworks/template-helpers”">​</a></h2><p><em>Set of common helpers (init views, etc.) for applications based on the <code>soundworks-template</code></em></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki monokai" style="background-color:#272822;color:#F8F8F2;" tabindex="0" dir="ltr"><code><span class="line"><span style="color:#A6E22E;">npm</span><span style="color:#E6DB74;"> install</span><span style="color:#AE81FF;"> --save</span><span style="color:#E6DB74;"> @soundworks/template-helpers</span></span></code></pre></div><p><a href="https://github.com/collective-soundworks/soundworks-template-build" target="_blank" rel="noreferrer"><strong>→ github</strong></a><br><a href="https://www.npmjs.com/package/@soundworks/template-build" target="_blank" rel="noreferrer"><strong>→ npm</strong></a></p>`,54)])])}const h=e(r,[["render",l]]);export{g as __pageData,h as default};
