<span class="line"><span style="color:#66D9EF;font-style:italic;">  const</span><span style="color:#F8F8F2;"> config </span><span style="color:#F92672;">=</span><span style="color:#A6E22E;"> loadConfig</span><span style="color:#F8F8F2;">();</span></span>
